import os
import machine, psoc6


# Try to mount the filesystem, and format the flash if it doesn't exist.
bdev = psoc6.Flash()

try:
    vfs = os.VfsFat(bdev)
except:
    os.VfsFat.mkfs(bdev)
    vfs = os.VfsFat(bdev)

os.mount(vfs, "/")

